function calculateTotals(frm) {
frappe.call({
        method: "frappe.client.get_list",
        args: {
            doctype: "Currency Exchange",
            filters: {
                date: frm.doc.date // Assuming you have a date field in your form
            },
            fields: ["exchange_rate"],
            limit_page_length: 1
        },
        callback: function(response) {
           // Define a currency formatter for USD
            var formatter = new Intl.NumberFormat('en-US', {
                style: 'currency',
                currency: 'USD',
                minimumFractionDigits: 2
            });
            
            var formatter2 = new Intl.NumberFormat('en-US', {
                        maximumFractionDigits: 2
            });

            var ex_rate = 1; // Default exchange rate if not found
            if (response.message && response.message.length > 0) {
                ex_rate = response.message[0].exchange_rate;
            }
            var total_qty = 0;
            var grand_total = 0;
            var grand_total_usd = 0;
            frm.doc.items.forEach(function(item) {
                item.amount = formatter.format(item.amount)
                  item.amount = item.qty * item.rate;
                 
                
                var real_dollars = item.qty * item.rate / ex_rate;
                item.amt_usd = formatter.format(real_dollars); // Adding $ symbol and formatting to 2 decimal places
                total_qty += item.qty;
                grand_total += item.amount;
                grand_total_usd += real_dollars;
            });

  
            frm.set_value('grand_total_usds', formatter2.format(grand_total_usd));
            frm.set_value('total_qty', total_qty);
            frm.set_value('grand_totals', grand_total);
            refresh_field('items');
        }
    });
}

