function calculateTotalsSupplier(frm) {
frappe.call({
method: "frappe.client.get_list",
args: {
doctype: "Currency Exchange",
filters: {
date: frm.doc.date // Assuming you have a date field in your form
},
fields: ["exchange_rate"],
limit_page_length: 1
},
callback: function(response) {
// Define a currency formatter for USD
var formatter = new Intl.NumberFormat('en-US', {
style: 'currency',
currency: 'USD',
minimumFractionDigits: 2
});
var ex_rate = 1; // Default exchange rate if not found
if (response.message && response.message.length > 0) {
ex_rate = response.message[0].exchange_rate;
}
var total_qty = 0;
var grand_total = 0;
var grand_total_usd = 0;
frm.doc.other_items.forEach(function(item) {
if (item.currency === "USD") {
    if (typeof item.exchange_rate === 'undefined' || item.exchange_rate === 3675) {
        item.exchange_rate = 0; // Set to default only if not already set or set to default
    }
    item.amount = item.qty * item.rate * item.exchange_rate;
} else {
    item.amount = item.qty * item.rate;
}

var real_dollars = item.amount / ex_rate;
item.amt_usd = formatter.format(real_dollars); // Adding $ symbol and formatting to 2 decimal places
total_qty += item.qty;
grand_total += item.amount;
grand_total_usd += real_dollars;
});
frm.set_value('total_quantity', total_qty);
frm.set_value('grand_total_ugx', grand_total);
frm.set_value('grand_total_usd', formatter.format(grand_total_usd));
refresh_field('other_items');
}
});
}

