frappe.ui.form.on('Purchase Management', {
    supplier: function(frm) {
        // Fetch supplier details
        frappe.call({
            method: 'frappe.client.get_list',
            args: {
                doctype: "Supplier",
                filters: {
                    name: frm.doc.supplier // Use the correct field name for supplier
                },
                fields: ["default_currency", "default_price_list"],
                limit_page_length: 1
            },
            callback: function(r) {
                if (r.message && r.message.length > 0) {
                    var supplierDetails = r.message[0];
                    var default_currency = supplierDetails.default_currency || "UGX"; // Set default currency to "UGX" if not found
                    var default_price_list = supplierDetails.default_price_list;

                    frm.set_value('price_list', default_price_list);
                    frm.set_value('currency', default_currency);

                    // After setting the supplier details, fetch item prices for both child tables
                    fetchItemPrices(frm, default_price_list, default_currency, 'items');
                    fetchOtherItemPrices(frm, 'other_items', default_currency);
                }
            }
        });
    }
});

function fetchItemPrices(frm, price_list, currency, child_table) {
    // Define default items
    var defaultItems = ['PMS', 'AGO', 'BIK'];

    // Fetch item prices for default items
    frappe.call({
        method: 'frappe.client.get_list',
        args: {
            doctype: "Item Price",
            filters: {
                item_code: ['in', defaultItems],
                price_list: price_list,
                buying: 1
            },
            fields: ["item_code", "price_list_rate"],
            limit_page_length: defaultItems.length
        },
        callback: function(r) {
            var priceListRates = {};
            if (r.message) {
                r.message.forEach(function(itemPrice) {
                    priceListRates[itemPrice.item_code] = itemPrice.price_list_rate;
                });
            }

            // Check if the items already exist in the child table
            var existingItems = frm.doc[child_table].map(function(d) { return d.item; });

            // Add default items to the child table if they don't already exist
            var defaultRows = defaultItems.map(function(itemCode) {
                if (!existingItems.includes(itemCode)) {
                    return {
                        item: itemCode,
                        qty: 1,
                        cost_center: "Fahaab Energy Depot Soroti - FEU",
                        rate: priceListRates[itemCode] || 0, // Set to 0 if price not found
                        warehouse: "PURCHASE STORE - FEU"
                    };
                }
            }).filter(function(row) { return row; }); // Filter out undefined rows

            // Add rows to the child table
            defaultRows.forEach(function(row) {
                frm.add_child(child_table, row);
            });

            // Refresh the child table to show the default rows
            frm.refresh_field(child_table);
        }
    });
}

function fetchOtherItemPrices(frm, child_table, default_currency) {
    // Define default items and their respective suppliers
    var defaultItems = ['URA Customs Duty', 'Fuel Making', 'Transport', 'Clearance'];
    var defaultSuppliers = ['Uganda Revenue Authority', 'Sicpa (U) Ltd', '', 'Ease Freight (U) Ltd'];

    // Fetch the default price list for each supplier
    var supplierPriceLists = {};
    var supplierCurrency = {};
    var priceListPromises = defaultSuppliers.map(function(supplier) {
        return new Promise(function(resolve) {
            frappe.call({
                method: 'frappe.client.get_list',
                args: {
                    doctype: "Supplier",
                    filters: { name: supplier },
                    fields: ["default_price_list", "default_currency"],
                    limit_page_length: 1
                },
                callback: function(r) {
                    if (r.message && r.message.length > 0) {
                        var supplierDetails = r.message[0];
                        var default_currency = supplierDetails.default_currency || "UGX"; // Set default currency to "UGX" if not found
                        var default_price_list = supplierDetails.default_price_list;

                        supplierPriceLists[supplier] = default_price_list;
                        supplierCurrency[supplier] = default_currency;
                    }
                    resolve();
                }
            });
        });
    });

    // After fetching all price lists, fetch the item prices
    Promise.all(priceListPromises).then(function() {
        var priceFetchPromises = defaultItems.map(function(itemCode, index) {
            var supplier = defaultSuppliers[index];
            var price_list = supplierPriceLists[supplier];

            return new Promise(function(resolve) {
                frappe.call({
                    method: 'frappe.client.get_list',
                    args: {
                        doctype: "Item Price",
                        filters: {
                            item_code: itemCode,
                            price_list: price_list,
                            buying: 1
                        },
                        fields: ["price_list_rate"],
                        limit_page_length: 1
                    },
                    callback: function(r) {
                        var price_list_rate = (r.message && r.message.length > 0) ? r.message[0].price_list_rate : 0;
                        // // var price_list_rate = 0;
                        // if (currency == "USD") {
                        //     price_list_rate = 3675; // Set rate to 36075 if currency is USD
                        // } 
                        resolve({
                            supplier: supplier,
                            item: itemCode,
                            qty: itemCode === 'Clearance' ? 1 : frm.doc.total_qtys || 1,
                            cost_center: "Fahaab Energy Depot Soroti - FEU",
                            rate: price_list_rate,
                            currency: supplierCurrency[supplier] || default_currency,
                            price_list: price_list
                        });
                    }
                });
            });
        });

        // Add fetched items to the child table
        Promise.all(priceFetchPromises).then(function(defaultRows) {
            var existingItems = frm.doc[child_table].map(function(d) { return d.item; });

            defaultRows.forEach(function(row) {
                if (!existingItems.includes(row.item)) {
                    frm.add_child(child_table, row);
                }
            });

            // Refresh the child table to show the default rows
            frm.refresh_field(child_table);
        });
    });
}

