frappe.ui.form.on('Fuel Sales App', {
    validate: function(frm) {
        let sale_date = frm.doc.date;
        let today = frappe.datetime.get_today();

        // Fetch user roles asynchronously
        frappe.call({
            method: 'frappe.client.get',
            args: {
                doctype: 'User',
                name: frappe.session.user
            },
            callback: function(r) {
                if (r.message) {
                    var user_roles = r.message.roles.map(role => role.role);

                    // Check if user has 'Dont Back Date' role
                    if (user_roles.includes('Dont Back Date')) {
                        // For users with 'Dont Back Date' role, validate sale date
                        if (!isDateWithinRange(sale_date, today)) {
                            frappe.msgprint(__('You can only save this document if the sale date is today, one day before, or one day after. Please Contact ACCOUNTS or choose an appropriate date.'));
                            frappe.validated = false;
                        }
                    }
                } else {
                    console.log('Error fetching user roles:', r);
                }
            }
        });
    }
});

// Function to check if a date is within one day before or one day after the reference date
function isDateWithinRange(date, referenceDate) {
    let formattedDate = frappe.datetime.str_to_obj(date);
    let formattedReferenceDate = frappe.datetime.str_to_obj(referenceDate);

    // Get the previous day and the next day
    let previousDay = new Date(formattedReferenceDate);
    previousDay.setDate(formattedReferenceDate.getDate() - 1);

    let nextDay = new Date(formattedReferenceDate);
    nextDay.setDate(formattedReferenceDate.getDate() + 1);

    // Check if the date is within range
    return (
        formattedDate.getFullYear() === formattedReferenceDate.getFullYear() &&
        formattedDate.getMonth() === formattedReferenceDate.getMonth() &&
        (formattedDate.getDate() === previousDay.getDate() ||
         formattedDate.getDate() === formattedReferenceDate.getDate() ||
         formattedDate.getDate() === nextDay.getDate())
    );
}

frappe.ui.form.on('Credit Sales App', {
    validate: function(frm) {
        let sale_date = frm.doc.date;
        let today = frappe.datetime.get_today();

        // Fetch user roles asynchronously
        frappe.call({
            method: 'frappe.client.get',
            args: {
                doctype: 'User',
                name: frappe.session.user
            },
            callback: function(r) {
                if (r.message) {
                    var user_roles = r.message.roles.map(role => role.role);

                    // Check if user has 'Dont Back Date' role
                    if (user_roles.includes('Dont Back Date')) {
                        // For users with 'Dont Back Date' role, validate sale date
                        if (!datesAreEqual(sale_date, today)) {
                            frappe.msgprint(__('You cannot save this document with the sale date set to {0}. Please Contact Accounts or choose today\'s date.', [sale_date]));
                            frappe.validated = false;
                        }
                    }
                } else {
                    console.log('Error fetching user roles:', r);
                }
            }
        });
    }
});

// Function to compare two dates
function datesAreEqual(date1, date2) {
    // Convert dates to YYYY-MM-DD format for comparison
    let formattedDate1 = frappe.datetime.str_to_obj(date1);
    let formattedDate2 = frappe.datetime.str_to_obj(date2);

    return formattedDate1.getFullYear() === formattedDate2.getFullYear() &&
           formattedDate1.getMonth() === formattedDate2.getMonth() &&
           formattedDate1.getDate() === formattedDate2.getDate();
}

