    update_discount: function (frm) {
        calculateDiscounts(frm);
    }
});

function calculateDiscounts(frm) {
    var total_discount = 0;

    // Loop through statement details and calculate total discount
    frm.doc.statement_details.forEach(function (item) {
        total_discount += item.additional_discount_amount || 0; // Safely handle undefined values
    });

    // Get the current additional_discount_amount
    var current_discount_amount = frm.doc.additional_discount_amount || 0;

    // Update the additional discount amount only if it has changed
    if (total_discount !== current_discount_amount) {
        frm.set_value('additional_discount_amount', total_discount);

        // Calculate the new total outstanding amount
        if (frm.doc.total_outstanding_amount) {
            var update_outstanding = frm.doc.total_outstanding_amount - (total_discount - current_discount_amount);
            frm.set_value('total_outstanding_amount', update_outstanding);
        }

        // Refresh the statement details field to show changes
        refresh_field('statement_details');
    }
}

