function fetchOrderNumber(frm) {
    // Call the Python method using Frappe's client API
    frappe.call({
        method: 'petro_station_app.custom_api.statement.fetch_order_numbers.fetch_order_numbers',
        args: {
            statement_name: frm.doc.name  // Pass the name of the Statement document
        },
        callback: function(response) {
            const order_numbers = response.message || [];
            // console.log(response);  // Log the response for debugging

            // Ensure that order_numbers is an array and has values
            if (order_numbers.length > 0) {
                let statement_details = frm.doc.statement_details || [];

                // Loop through the statement details child table
                statement_details.forEach((detail, index) => {
                    // Check if we have a valid order number at the current index
                    if (order_numbers[index]) {
                        // Update the order_no field in the Statement Details child table
                        frappe.model.set_value(detail.doctype, detail.name, 'order_no', order_numbers[index]);
                    }
                });

                // Refresh the field to show the updated data
                frm.refresh_field('statement_details');
            }
        }
    });
}


